const beta = readVariable("BETA", 0.5);
const deltaT = readVariable("DELTA", 3.5);
const baseline = readVariable("BASELINE", 0.65);

if (msg.topic == "t1" && msg.payload != null) {
    flow.set("t1", msg.payload)
}
if (msg.topic == "t2" && msg.payload != null) {
    flow.set("t2", msg.payload)
}
if ((msg.topic == "target" || msg.topic == "tset") && msg.payload != null) {
    flow.set("tset", msg.payload)
}
if (typeof msg.payload === 'object') {
    if (msg.payload.t1 != null) {
        flow.set("t1", msg.payload.t1)
    }
    if (msg.payload.t2 != null) {
        flow.set("t2", msg.payload.t2)
    }
    if (msg.payload.target != null) {
        flow.set("tset", msg.payload.target)
    }
    if (msg.payload.tset != null) {
        flow.set("tset", msg.payload.tset)
    }
}

let t1 = flow.get("t1")
let t2 = flow.get("t2")
let tset = flow.get("tset")

let temperature = null

if (t1 == undefined && t2 == undefined) {
    return
} else if (t1 == undefined && t2 != undefined) {
    temperature = t2    
} else if (t1 != undefined && t2 == undefined) {
    temperature = t1
} else {
    let alpha = calculateAlpha(t1, tset)
    temperature = alpha * t1 + (1 - alpha) * t2
    temperature = Math.round(temperature * 100) / 100
}

return {
    target: tset,
    payload: temperature,
    t1,
    t2
}

function calculateAlpha(x, target) {
    if (target == null) {
        return 0.75
    }
    let alpha = baseline + (1 - baseline) * (1 / (1 + Math.exp(beta * Math.abs(target - x) - deltaT)))
    return alpha
}

function readVariable(name, def) {
    let fromEnv = env.get(name)
    if (fromEnv != null) {
        return fromEnv
    } else {
        return def
    }
}
